import { redirect } from "react-router-dom"
function getTokenDuration() {
  const storageTokenDuration = localStorage.getItem("expiration")
  const tokenDuration = storageTokenDuration.Date.now()
  console.log(tokenDuration)
}

export const getUserToken = () => {
  const token = localStorage.getItem("token")
  return token
}

export const tokenLoader = () => {
  return getUserToken()
}

export function checkAuthLoader() {
  // this function will be added in the next lecture
  // make sure it looks like this in the end
  const token = getUserToken()

  if (!token) {
    return redirect("/auth")
  }

  return null // this is missing in the next lecture video and should be added by you
}
