import { redirect } from "react-router-dom"
export function getTokenDuration() {
  const storageTokenDuration = localStorage.getItem("expiration")
  const expirationDate = new Date(storageTokenDuration)
  const currentDate = new Date()
  const tokenDuration = expirationDate.getTime() - currentDate.getTime()
  return tokenDuration
}

export const getUserToken = () => {
  const token = localStorage.getItem("token")
  const tokenDuration = getTokenDuration()
  if (tokenDuration < 0) {
    return "EXPIRED"
  }
  return token
}

export const tokenLoader = () => {
  return getUserToken()
}

export function checkAuthLoader() {
  // this function will be added in the next lecture
  // make sure it looks like this in the end
  const token = getUserToken()

  if (!token) {
    return redirect("/auth")
  }

